import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Inventory.css";

const API_URL = "http://127.0.0.1:5000";

function Products() {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState("");
  const [editProduct, setEditProduct] = useState(null);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    const res = await axios.get(`${API_URL}/products/`);
    setProducts(res.data);
  };

  const addOrUpdateProduct = async () => {
    if (editProduct) {
      await axios.put(`${API_URL}/products/${editProduct.id}`, { name: newProduct });
      setEditProduct(null);
    } else {
      await axios.post(`${API_URL}/products/`, { name: newProduct });
    }
    setNewProduct("");
    fetchProducts();
  };

  const handleEdit = (product) => {
    setEditProduct(product);
    setNewProduct(product.name);
  };

  const handleDelete = async (id) => {
    await axios.delete(`${API_URL}/products/${id}`);
    fetchProducts();
  };

  return (
    <div className="page">
      <h2>Products</h2>
      <input
        value={newProduct}
        onChange={(e) => setNewProduct(e.target.value)}
        placeholder="Product Name"
      />
      <button onClick={addOrUpdateProduct}>{editProduct ? "Update" : "Add"}</button>

      <ul>
        {products.map((p) => (
          <li key={p.id}>
            {p.name}{" "}
            <button onClick={() => handleEdit(p)}>Edit</button>{" "}
            <button onClick={() => handleDelete(p.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Products;
