const API_URL = "http://127.0.0.1:5000"; // ✅ Must match backend

const fetchData = async () => {
  try {
    const [p, l, m] = await Promise.all([
      axios.get(`${API_URL}/products/`),
      axios.get(`${API_URL}/locations/`),
      axios.get(`${API_URL}/product-movements/`),
    ]);
    setProducts(p.data);
    setLocations(l.data);
    setMovements(m.data);
  } catch (error) {
    console.error("Fetch error:", error);
  }
};

const addOrUpdateMovement = async () => {
  try {
    if (!movementData.product_id || !movementData.quantity) return;

    if (editMovement) {
      await axios.put(`${API_URL}/product-movements/${editMovement.id}`, movementData);
      setEditMovement(null);
    } else {
      await axios.post(`${API_URL}/product-movements/`, movementData);
    }

    setMovementData({
      product_id: "",
      from_location_id: "",
      to_location_id: "",
      quantity: "",
      movement_date: "",
    });

    fetchData();
  } catch (error) {
    console.error("Add/Update error:", error);
  }
};
