import React, { useState, useEffect } from "react";
import "./App.css";

import axios from "axios";

const API_URL = "http://127.0.0.1:5000";

function App() {
  const [products, setProducts] = useState([]);
  const [locations, setLocations] = useState([]);
  const [movements, setMovements] = useState([]);

  const [newProduct, setNewProduct] = useState("");
  const [newLocation, setNewLocation] = useState("");
  const [movementData, setMovementData] = useState({
    product_id: "",
    from_location_id: "",
    to_location_id: "",
    quantity: "",
    movement_date: "",
  });

  // Fetch data
  useEffect(() => {
    fetchProducts();
    fetchLocations();
    fetchMovements();
  }, []);

  const fetchProducts = async () => {
    const res = await axios.get(`${API_URL}/products/`);
    setProducts(res.data);
  };

  const fetchLocations = async () => {
    const res = await axios.get(`${API_URL}/locations`);
    setLocations(res.data);
  };

  const fetchMovements = async () => {
    const res = await axios.get(`${API_URL}/product-movements`);
    setMovements(res.data);
  };

  // Handlers
  const addProduct = async () => {
    await axios.post(`${API_URL}/products/`, { name: newProduct });
    setNewProduct("");
    fetchProducts();
  };

  const addLocation = async () => {
    await axios.post(`${API_URL}/locations`, { name: newLocation });
    setNewLocation("");
    fetchLocations();
  };

  const addMovement = async () => {
    await axios.post(`${API_URL}/product-movements`, movementData);
    setMovementData({
      product_id: "",
      from_location_id: "",
      to_location_id: "",
      quantity: "",
      movement_date: "",
    });
    fetchMovements();
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Products</h2>
      <input
        value={newProduct}
        onChange={(e) => setNewProduct(e.target.value)}
        placeholder="Product Name"
      />
      <button onClick={addProduct}>Add Product</button>
      <ul>
        {products.map((p) => (
          <li key={p.id}>{p.name}</li>
        ))}
      </ul>

      <h2>Locations</h2>
      <input
        value={newLocation}
        onChange={(e) => setNewLocation(e.target.value)}
        placeholder="Location Name"
      />
      <button onClick={addLocation}>Add Location</button>
      <ul>
        {locations.map((l) => (
          <li key={l.id}>{l.name}</li>
        ))}
      </ul>

      <h2>Product Movements</h2>
      <select
        value={movementData.product_id}
        onChange={(e) =>
          setMovementData({ ...movementData, product_id: e.target.value })
        }
      >
        <option value="">Select Product</option>
        {products.map((p) => (
          <option key={p.id} value={p.id}>
            {p.name}
          </option>
        ))}
      </select>

      <select
        value={movementData.from_location_id}
        onChange={(e) =>
          setMovementData({ ...movementData, from_location_id: e.target.value })
        }
      >
        <option value="">From Location</option>
        {locations.map((l) => (
          <option key={l.id} value={l.id}>
            {l.name}
          </option>
        ))}
      </select>

      <select
        value={movementData.to_location_id}
        onChange={(e) =>
          setMovementData({ ...movementData, to_location_id: e.target.value })
        }
      >
        <option value="">To Location</option>
        {locations.map((l) => (
          <option key={l.id} value={l.id}>
            {l.name}
          </option>
        ))}
      </select>

      <input
        type="number"
        placeholder="Quantity"
        value={movementData.quantity}
        onChange={(e) =>
          setMovementData({ ...movementData, quantity: e.target.value })
        }
      />
      <input
        type="date"
        value={movementData.movement_date}
        onChange={(e) =>
          setMovementData({ ...movementData, movement_date: e.target.value })
        }
      />
      <button onClick={addMovement}>Add Movement</button>

      <ul>
        {movements.map((m) => (
          <li key={m.id}>
            Product ID: {m.product_id}, From: {m.from_location_id}, To:{" "}
            {m.to_location_id}, Qty: {m.quantity}, Date: {m.movement_date}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
