import React, { useEffect, useState } from "react";
import axios from "axios";
import { Package, MapPin, RefreshCcw, Boxes } from "lucide-react";

const Dashboard = () => {
  const [products, setProducts] = useState([]);
  const [locations, setLocations] = useState([]);
  const [movements, setMovements] = useState([]);
  const [loading, setLoading] = useState(true);

  const BASE_URL = "http://127.0.0.1:5000";

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [prodRes, locRes, moveRes] = await Promise.all([
          axios.get(`${BASE_URL}/products/`),
          axios.get(`${BASE_URL}/locations/`),
          axios.get(`${BASE_URL}/product-movements/`)
        ]);
        setProducts(prodRes.data || []);
        setLocations(locRes.data || []);
        setMovements(moveRes.data || []);
      } catch (error) {
        console.error("Error fetching data:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  if (loading) return <p className="text-center mt-10 text-gray-500">Loading dashboard...</p>;

  // Calculate totals
  const totalStock = movements.reduce((sum, move) => sum + (move.qty || 0), 0);

  const summaryCards = [
    { title: "Total Products", value: products.length, icon: <Package className="w-6 h-6 text-indigo-500" /> },
    { title: "Total Locations", value: locations.length, icon: <MapPin className="w-6 h-6 text-green-500" /> },
    { title: "Total Movements", value: movements.length, icon: <RefreshCcw className="w-6 h-6 text-yellow-500" /> },
    { title: "Total Stock", value: totalStock, icon: <Boxes className="w-6 h-6 text-pink-500" /> },
  ];

  return (
    <div className="p-6 bg-gray-50 min-h-screen">
      <h1 className="text-3xl font-bold mb-6 text-gray-800">ðŸ“Š Inventory Dashboard</h1>

      {/* Summary Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-8">
        {summaryCards.map((card, idx) => (
          <div
            key={idx}
            className="bg-white shadow-md rounded-2xl p-4 flex items-center gap-4 hover:shadow-lg transition"
          >
            <div>{card.icon}</div>
            <div>
              <p className="text-sm text-gray-500">{card.title}</p>
              <p className="text-2xl font-semibold text-gray-800">{card.value}</p>
            </div>
          </div>
        ))}
      </div>

      {/* Recent Movements */}
      <div className="bg-white rounded-2xl shadow-md p-6">
        <h2 className="text-xl font-semibold text-gray-700 mb-4">Recent Product Movements</h2>
        <table className="min-w-full text-sm text-gray-700">
          <thead>
            <tr className="border-b bg-gray-100">
              <th className="p-2 text-left">Movement ID</th>
              <th className="p-2 text-left">Product</th>
              <th className="p-2 text-left">From</th>
              <th className="p-2 text-left">To</th>
              <th className="p-2 text-left">Qty</th>
            </tr>
          </thead>
          <tbody>
            {movements.slice(-10).reverse().map((move, idx) => {
              const product = products.find(p => p.product_id === move.product_id);
              return (
                <tr key={idx} className="border-b hover:bg-gray-50">
                  <td className="p-2">{move.movement_id}</td>
                  <td className="p-2">{product ? product.name : move.product_id}</td>
                  <td className="p-2">{move.from_location || "-"}</td>
                  <td className="p-2">{move.to_location || "-"}</td>
                  <td className="p-2 font-semibold text-indigo-600">{move.qty}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Dashboard;
