import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Movements.css";

const API_URL = "http://127.0.0.1:5000";

function Movements() {
  const [products, setProducts] = useState([]);
  const [locations, setLocations] = useState([]);
  const [movements, setMovements] = useState([]);
  const [editMovement, setEditMovement] = useState(null);

  const [movementData, setMovementData] = useState({
    product_id: "",
    from_location_id: "",
    to_location_id: "",
    quantity: "",
    movement_date: "",
  });

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [p, l, m] = await Promise.all([
        axios.get(`${API_URL}/products/`),
        axios.get(`${API_URL}/locations/`),
        axios.get(`${API_URL}/product-movements/`),
      ]);
      setProducts(p.data);
      setLocations(l.data);
      setMovements(m.data);
    } catch (error) {
      console.error("Fetch error:", error);
    }
  };

  const addOrUpdateMovement = async () => {
    if (!movementData.product_id || !movementData.quantity) return;

    try {
      if (editMovement) {
        await axios.put(`${API_URL}/product-movements/${editMovement.id}`, movementData);
        setEditMovement(null);
      } else {
        await axios.post(`${API_URL}/product-movements/`, movementData);
      }

      setMovementData({
        product_id: "",
        from_location_id: "",
        to_location_id: "",
        quantity: "",
        movement_date: "",
      });

      fetchData();
    } catch (error) {
      console.error("Add/Update error:", error);
    }
  };

  const handleEdit = (m) => {
    setEditMovement(m);
    setMovementData({
      product_id: m.product_id || "",
      from_location_id: m.from_location_id || "",
      to_location_id: m.to_location_id || "",
      quantity: m.quantity || "",
      movement_date: m.movement_date || "",
    });
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`${API_URL}/product-movements/${id}`);
      fetchData();
    } catch (error) {
      console.error("Delete error:", error);
    }
  };

  return (
    <div className="movement-page">
      <h2 className="movement-title">Product Movements</h2>

      <div className="movement-inputs">
        <select
          className="movement-input"
          value={movementData.product_id}
          onChange={(e) => setMovementData({ ...movementData, product_id: e.target.value })}
        >
          <option value="">Select Product</option>
          {products.map((p) => (
            <option key={p.id} value={p.id}>{p.name}</option>
          ))}
        </select>

        <select
          className="movement-input"
          value={movementData.from_location_id}
          onChange={(e) => setMovementData({ ...movementData, from_location_id: e.target.value })}
        >
          <option value="">From Location</option>
          {locations.map((l) => (
            <option key={l.id} value={l.id}>{l.name}</option>
          ))}
        </select>

        <select
          className="movement-input"
          value={movementData.to_location_id}
          onChange={(e) => setMovementData({ ...movementData, to_location_id: e.target.value })}
        >
          <option value="">To Location</option>
          {locations.map((l) => (
            <option key={l.id} value={l.id}>{l.name}</option>
          ))}
        </select>

        <input
          type="number"
          className="movement-input"
          placeholder="Quantity"
          value={movementData.quantity}
          onChange={(e) => setMovementData({ ...movementData, quantity: e.target.value })}
        />

        <input
          type="date"
          className="movement-input"
          value={movementData.movement_date}
          onChange={(e) => setMovementData({ ...movementData, movement_date: e.target.value })}
        />

        <button className="movement-add-btn" onClick={addOrUpdateMovement}>
          {editMovement ? "Update" : "Add"}
        </button>
      </div>

      <div className="movement-list">
        {movements.map((m) => (
          <div className="movement-item" key={m.id}>
            <span>
              Product: {m.product_name || m.product_id}, From: {m.from_location_name || "窶能

