              <button className="movement-edit-btn" onClick={() => handleEdit(m)}>Edit</button>
              <button className="movement-delete-btn" onClick={() => handleDelete(m.id)}>Delete</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

